<script>
import Main from "../components/personal_account/main.vue";
import Verification from "../components/personal_account/verification.vue";
import Investment from "../components/personal_account/investment.vue";
import Team from "../components/personal_account/team.vue";
import Status from "../components/personal_account/status.vue";
import Finance from "../components/personal_account/finance.vue";

export default {
  components: {
    Main,
    Verification,
    Investment,
    Team,
    Status,
    Finance,
  },
  data: function () {
    return {
      pages: {
        Main: true,
        Verification: false,
        Investment: false,
        Team: false,
        Status: false,
        Finance: false,
      },
      photos: ["card1.png", "card1.png", "card1.png"],
      currentPhoto: 0,
    };
  },
  methods: {
    changePage(page) {
      for (const key in this.pages) {
        this.pages[key] = false;
      }
      this.pages[page] = true;
    },
    leftClick: function () {
      this.transition();
      setTimeout(() => {
        if (this.currentPhoto > 0) {
          this.currentPhoto--;
        } else {
          this.currentPhoto = this.photos.length - 1;
        }
      }, 500);
    },
    rightClick: function () {
      this.transition();
      setTimeout(() => {
        if (this.currentPhoto < this.photos.length - 1) {
          this.currentPhoto++;
        } else {
          this.currentPhoto = 0;
        }
      }, 500);
    },
    transition: function () {
      this.$refs.image.style.opacity = 0;
      setTimeout(() => (this.$refs.image.style.opacity = 1), 499);
    },
  },
};
</script>

